import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchCurrencies } from '../actions';
import Input from '../components/Input';
import Select from '../components/Select';
import getCurrencyInfo from '../services/currencyAPI';

class Wallet extends Component {
  constructor() {
    super();

    this.fetchCurrenciesToState = this.fetchCurrenciesToState.bind(this);

    this.state = {
      totalExpenses: 0,
      actualCurrency: 'BRL',
    };
  }

  componentDidMount() {
    const { fetchCurrenciesToProps } = this.props;
    this.fetchCurrenciesToState();
    fetchCurrenciesToProps();
  }

  async fetchCurrenciesToState() {
    const currencyInfo = await getCurrencyInfo();
    this.setState({
      currencies: Object.keys(currencyInfo),
    });
  }

  render() {
    const { userEmail/* , walletCurrencies */ } = this.props;
    const payMethods = ['Dinheiro', 'Cartão de crédito', 'Cartão de débito'];
    const tags = ['Alimentação', 'Lazer', 'Trabalho', 'Transporte', 'Saúde'];
    const { currencies, totalExpenses, actualCurrency } = this.state;
    return (
      <section>
        <header>
          <span data-testid="email-field">{ userEmail }</span>
          <span data-testid="total-field">{ totalExpenses }</span>
          <span data-testid="header-currency-field">{ actualCurrency }</span>
        </header>
        <form>
          <Input
            type="text"
            name="Valor"
          />
          <Input
            type="text"
            name="Descrição"
          />
          <Select
            name="Moeda"
            options={ currencies }
          />
          <Select
            name="Método de pagamento"
            options={ payMethods }
          />
          <Select
            name="Tag"
            options={ tags }
          />
        </form>
      </section>
    );
  }
}

Wallet.propTypes = {
  // fetchCurrencies: PropTypes.func.isRequired,
  fetchCurrenciesToProps: PropTypes.func.isRequired,
  userEmail: PropTypes.string.isRequired,
  // walletCurrencies: PropTypes.arrayOf(PropTypes.string).isRequired,
};

const mapStateToProps = (state) => ({
  userEmail: state.user.email,
  walletCurrencies: state.wallet.currencies,
});

const mapDispatchToProps = (dispatch) => ({
  fetchCurrenciesToProps: () => dispatch(fetchCurrencies()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);
