import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Input from '../components/Input';
import Select from '../components/Select';
import { fetchCurrencies } from '../actions';
import getCurrencyInfo from '../services/currencyAPI';

class Wallet extends React.Component {
  constructor() {
    super();
    this.state = {
      currencies: [],
      totalExpenses: 0,
      actualCurrency: 'BRL',
    };
  }

  componentDidMount() {
    // const { fetchCurrencies } = this.props;
    // fetchCurrencies();
    const currencyInfo = getCurrencyInfo();
    const currenciesCodes = currencyInfo.map((currency) => currency.code);
    this.setState({
      currencies: [...currenciesCodes],
    });
  }

  render() {
    const { userEmail, walletCurrencies } = this.props;
    const payMethods = ['Dinheiro', 'Cartão de crédito', 'Cartão de débito'];
    const tags = ['Alimentação', 'Lazer', 'Trabalho', 'Transporte', 'Saúde'];
    const { totalExpenses, actualCurrency } = this.state;
    return (
      <section>
        <header>
          <span data-testid="email-field">{ userEmail }</span>
          <span data-testid="total-field">{ totalExpenses }</span>
          <span data-testid="header-currency-field">{ actualCurrency }</span>
        </header>
        <form>
          <Input
            type="text"
            name="Valor"
          />
          <Input
            type="text"
            name="Descrição"
          />
          <Select
            name="Moeda"
            options={ walletCurrencies }
          />
          <Select
            name="Método de pagamento"
            options={ payMethods }
          />
          <Select
            name="Tag"
            options={ tags }
          />
        </form>
      </section>
    );
  }
}

Wallet.propTypes = {
  fetchCurrencies: PropTypes.func.isRequired,
  userEmail: PropTypes.string.isRequired,
  walletCurrencies: PropTypes.arrayOf(PropTypes.string).isRequired,
};

const mapStateToProps = (state) => ({
  userEmail: state.user.email,
  walletCurrencies: state.wallet.currencies,
});

const mapDispatchToProps = (dispatch) => ({
  fetchCurrencies: () => dispatch(fetchCurrencies()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);
