import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { setUserEmail } from '../actions';

class Login extends Component {
  constructor() {
    super();

    this.handleClick = this.handleClick.bind(this);
  }

  handleChange({ target: { value } }) {
    const { setUserEmailLogin } = this.props;

    setUserEmailLogin(value);
  }

  handleClick(email) {
    const regexEmail = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/;
    return regexEmail.test(email);
  }

  render() {
    return (
      <section>
        <form>
          <label htmlFor="email">
            Email
            <input type="email" name="email" data-testid="email-input" />
          </label>
          <label htmlFor="password">
            Senha
            <input type="password" name="password" data-testid="password-input" />
          </label>
          <button type="button">Entrar</button>
        </form>
      </section>
    );
  }
}

Login.propTypes = {
  setUserEmailLogin: PropTypes.func.isRequired,
};

// const mapStateToProps = (state) => ({
//   email: state.user.email,
// });

const mapDispatchToProps = (dispatch) => ({
  setUserEmailLogin: (email) => dispatch(setUserEmail(email)),
});

export default connect(_, mapDispatchToProps)(Login);
