import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { setUserEmail, setUserPassword } from '../actions';

class Login extends Component {
  constructor() {
    super();

    this.handleEmailChange = this.handleEmailChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleEmailValidation = this.handleEmailValidation.bind(this);
    this.handlePasswordValidation = this.handlePasswordValidation.bind(this);
  }

  handleEmailChange({ target: { value } }) {
    const { setUserEmailLogin } = this.props;
    setUserEmailLogin(value);
  }

  handlePasswordChange({ target: { value } }) {
    const { setUserPasswordLogin } = this.props;
    setUserPasswordLogin(value);
  }

  handleEmailValidation(email) {
    const regexEmail = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/;
    return regexEmail.test(email);
  }

  handlePasswordValidation(password) {
    const NUMBER_SIX = 6;
    return (password.length >= NUMBER_SIX);
  }

  render() {
    const { userEmail, userPassword } = this.props;
    const isEmailValid = this.handleEmailValidation(userEmail);
    const isPasswordValid = this.handlePasswordValidation(userPassword);
    return (
      <section>
        <form>
          <label htmlFor="email">
            Email
            <input
              type="email"
              name="email"
              data-testid="email-input"
              onChange={ this.handleEmailChange }
            />
          </label>
          <label htmlFor="password">
            Senha
            <input
              type="password"
              name="password"
              data-testid="password-input"
              onChange={ this.handlePasswordChange }
            />
          </label>
          <button
            type="button"
            disabled={ !isEmailValid && !isPasswordValid }
          >
            Entrar

          </button>
        </form>
      </section>
    );
  }
}

Login.propTypes = {
  setUserEmailLogin: PropTypes.func.isRequired,
  setUserPasswordLogin: PropTypes.func.isRequired,
  userEmail: PropTypes.string.isRequired,
  userPassword: PropTypes.string.isRequired,
};

const mapStateToProps = (state) => ({
  userEmail: state.userReducer.user.email,
  userPassword: state.userReducer.user.password,
});

const mapDispatchToProps = (dispatch) => ({
  setUserEmailLogin: (email) => dispatch(setUserEmail(email)),
  setUserPasswordLogin: (password) => dispatch(setUserPassword(password)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Login);
