import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchCurrencies } from '../actions';
import Input from '../components/Input';
import Select from '../components/Select';

class Wallet extends Component {
  constructor() {
    super();

    this.handleInputChange = this.handleInputChange.bind(this);

    this.state = {
      totalExpenses: 0,
      // currentExpense: [],
      valor: 0,
      descricao: '',
      moeda: '',
      payMethod: '',
      tag: '',
      actualCurrency: 'BRL',
    };
  }

  componentDidMount() {
    const { fetchCurrenciesToProps } = this.props;
    fetchCurrenciesToProps();
  }

  handleInputChange({ target: { name, value } }) {
    this.setState({
      [name]: value,
    });
  }

  render() {
    const { props: { userEmail, walletCurrencies }, state: {
      totalExpenses, actualCurrency, valor, descricao, moeda, payMethod, tag }, handleInputChange } = this;
    const payMethods = ['Dinheiro', 'Cartão de crédito', 'Cartão de débito'];
    const tags = ['Alimentação', 'Lazer', 'Trabalho', 'Transporte', 'Saúde'];
    return (
      <section>
        <header>
          <span data-testid="email-field">{ userEmail }</span>
          <span data-testid="total-field">{ totalExpenses }</span>
          <span data-testid="header-currency-field">{ actualCurrency }</span>
        </header>
        <form>
          <Input
            type="text"
            name="valor"
            value={ valor }
            labelText="Valor"
            onChange={ handleInputChange } />
          <Input
            type="text"
            name="descricao"
            value={ descricao }
            labelText="Descrição"
            onChange={ handleInputChange }
          />
          <Select
            name="moeda"
            value={ moeda }
            labelText="Moeda"
            options={ walletCurrencies.filter((currency) => currency !== 'USDT') }
            onChange={ handleInputChange }
          />
          <Select
            name="payMethod"
            value={ payMethod }
            labelText="Método de pagamento"
            options={ payMethods }
            onChange={ handleInputChange }
          />
          <Select
            name="tag"
            value={ tag }
            labelText="Tag"
            options={ tags }
            onChange={ handleInputChange }
          />
          <button type="button">Adicionar despesa</button>
        </form>
      </section>
    );
  }
}

Wallet.propTypes = {
  fetchCurrenciesToProps: PropTypes.func.isRequired,
  userEmail: PropTypes.string.isRequired,
  walletCurrencies: PropTypes.arrayOf(PropTypes.string).isRequired,
};

const mapStateToProps = (state) => ({
  userEmail: state.user.email,
  walletCurrencies: state.wallet.currencies,
});

const mapDispatchToProps = (dispatch) => ({
  fetchCurrenciesToProps: () => dispatch(fetchCurrencies()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);
