import getCurrencyInfo from '../services/currencyAPI';

export const SET_USER_EMAIL = 'SET_USER_EMAIL';
export const SET_USER_PASSWORD = 'SET_USER_PASSWORD';
export const GET_CURRENCIES = 'GET_CURRENCIES';
export const RECEIVE_CURRENCIES_INFO = 'RECEIVE_CURRENCIES_INFO';

export const setUserEmail = (payload) => ({
  type: SET_USER_EMAIL,
  payload,
});
export const setUserPassword = (payload) => ({
  type: SET_USER_PASSWORD,
  payload,
});

export const getCurrencies = (payload) => ({
  type: GET_CURRENCIES,
  payload,
});

export const receiveCurrenciesInfo = (currencyCode) => ({
  type: RECEIVE_CURRENCIES_INFO,
  currencyCode,
});

export const fetchCurrencies = () => ( dispatch) => {
  getCurrencyInfo()
    .then((currencyInfo) => {
      console.log(currencyInfo);
      const currencyCode = currencyInfo.map((currency) => currency.code);
      dispatch(receiveCurrenciesInfo(currencyCode));
    });
};
