import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import logo from '../images/toninvest.png';

export default class Header extends Component {
  render() {
    const { userEmail, expenses } = this.props;
    return (
      <header
        className="w-full h-20 flex justify-between
      bg-gradient-to-r  px-4 py-4 text-gray-50 items-center"
      >
        <div className="w-1/6 h/full">
          <Link to="/">
            <img src={ logo } alt="toninvest" />
          </Link>
        </div>
        <div>
          <span data-testid="email-field" className="block">{ userEmail }</span>
          <span data-testid="total-field">
            { expenses.reduce((acc, curr) => {
              acc += (curr.exchangeRates[curr.currency].ask * curr.value);
              return acc;
            }, 0).toFixed([2]) }
          </span>
          <span data-testid="header-currency-field">BRL</span>
        </div>
      </header>
    );
  }
}

Header.propTypes = {
  expenses: PropTypes.shape({
    reduce: PropTypes.func,
  }).isRequired,
  userEmail: PropTypes.string.isRequired,
};
